     * @dev Override approve to include blacklist checks
     */
    function approve(address spender, uint256 amount) 
        public 
        override 
        notBlacklisted(msg.sender) 
        notBlacklisted(spender) 
        returns (bool) 
    {
        return super.approve(spender, amount);
    }

